int getSNR(int source, int dest){
    int i;
    for (i = 0; i < MAX_LINKS; i++){
        if (environment.linksSNR[i].source == source & environment.linksSNR[i].destination == dest){
            return environment.linksSNR[i].SNR;
        }
    }
    return -50;
}

const int MAX_RUN = 30;
int expectedPackets[MAX_RUN];
int queueLost[MAX_RUN];
int recvdPackets[MAX_RUN];

typedef struct{
    int period;
}Packet;
Packet packet;

typedef struct{
    Packet packets[MAX_QUEUE];
    int size;
}Queue;

typedef struct{
    Packet packets[MAX_LOAD];
    int size;
}BufferQ;

BufferQ bufferQ;
typedef struct{
    Packet packets[MAX_SLOTS];
    int size;
}SendQ;
SendQ sendQ, linkQ;


//Queue queue[mote_ids];

void addBuffer(SendQ &sendQ, BufferQ &bufferQ){
    int i;

    for (i = 0; i < bufferQ.size; i++){
        sendQ.packets[i] = bufferQ.packets[i];
    }
    sendQ.size = bufferQ.size;
    expectedPackets[periods]+= bufferQ.size;
}

void addRecvQ(SendQ &sendQ, Queue &recvQ){
    int i, r = 0;
    int initialSize = sendQ.size;
    int endSize = recvQ.size + initialSize;

    if (recvQ.size == 0) return;
    if (endSize > MAX_SLOTS){
        endSize = MAX_SLOTS;
    }


    for (i = initialSize; i < endSize; i++){
        sendQ.packets[i] = recvQ.packets[r++];
    }
    sendQ.size = endSize;

    // Move recv queue packets to head of queue
    i = 0;
    if (r <= recvQ.size){
        endSize = recvQ.size - r;
        while (r < recvQ.size){
            if (periods - recvQ.packets[r].period >= THRESHOLD){
                r++;
                endSize--;
            }
            else{
                recvQ.packets[i++] = recvQ.packets[r++];
            }
        }
        recvQ.size = endSize;
    }
}

void sendPackets(int load, SendQ &sendQ, SendQ &linkQ){
    int i;
    for (i = 0; i < load; i++){
        linkQ.packets[i] = sendQ.packets[i];
    }
    linkQ.size = load;
    if (load == sendQ.size) {sendQ.size = 0; return;}
    if (load != sendQ.size){
        int moveL = load; // move location
        int newSize = sendQ.size - load;
        for(i = 0; i < newSize; i++){
            sendQ.packets[i] = sendQ.packets[moveL++];
        }
        sendQ.size = newSize;
    }
}

template.declaration:
int parent = 0;
//Link link;
int parentLoad;
//int queue;
Queue recvQ;

Mote mote;

bool hasLink(){
    int i;
    for(i = parent+1; i < mote.parents; i++){
        if (mote.links[i] != EMPTY_LINK){
            return true;
        }
    }
    return false;
}

int calcParentLoad(Link link){
    // if there is no next link then current load should be used for this link
    if (!hasLink())
        return sendQ.size;
    else if (sendQ.size == 0)
        return 0;
    else
        return sendQ.size * link.distribution/100;
}

Link selectNextLink(){
    int i;
    for(i = parent+1; i < mote.parents; i++){
        if (mote.links[i] != EMPTY_LINK){
            parent = i;
            return mote.links[i];
        }
    }
    return EMPTY_LINK;
}

void createBuffer(){
    //return mote.load;
    int i;
    bufferQ.size = mote.load;
    for (i = 0; i < mote.load; i++){
        bufferQ.packets[i].period = periods;
    }
}

void initialize(){
    mote = deltaIoT.motes[moteId];
    parent = -1;
    createBuffer();
    sendQ.size = 0;
    linkQ.size = 0;
}

void receivePackets(Packet &packet){
    if (recvQ.size < MAX_QUEUE){
        recvQ.packets[recvQ.size] = packet;
        recvQ.size++;
    }
}

